const { generateStrongPassword, isStrongPassword } = require('./index');

describe('generateStrongPassword', () => {
  test('should generate a password', () => {
    const password = generateStrongPassword(12);
    expect(/[A-Z]/.test(password) && /[a-z]/.test(password) && 
    /\d/.test(password) && /[!@#$%^&*()_+]/.test(password)).toBe(true); 
  });

  test('generateStrongPassword should always return a strong password', () => {
    for (let i = 0; i < 100; i++) {
      const password = generateStrongPassword(12);
      expect(isStrongPassword(password)).toBe(true);
    }
  });
});

describe('isStrongPassword', () => {
  test('is false for a password shorter than 8 characters', () => {
    const shortPassword = 'if!!';  // a short password
    expect(isStrongPassword(shortPassword)).toBe(false);
  });
});